package com.tramnt.genart.data.repository

import com.tramnt.genart.data.api.StyleApiService
import com.tramnt.genart.domain.mapper.toStyleCategories
import com.tramnt.genart.domain.model.StyleCategory
import com.tramnt.genart.domain.repository.StyleRepository
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.flow

class StyleRepositoryImpl(
 private val apiService: StyleApiService
) : StyleRepository {

 override suspend fun getStyles(): Flow<Result<List<StyleCategory>>> = flow {
 try {
 val response = apiService.getStyles()
 if (response.status == 200) {
 emit(Result.success(response.data.toStyleCategories()))
 } else {
 emit(Result.failure(Exception(response.message)))
 }
 } catch (e: Exception) {
 emit(Result.failure(e))
 }
 }
}
