+package com.tramnt.genart.data.repository

+import android.content.Context
+import android.net.Uri
+import com.tramnt.genart.data.api.AiArtApiService
+import com.tramnt.genart.domain.mapper.toDomain
+import com.tramnt.genart.domain.model.AiArt
+import com.tramnt.genart.domain.model.Signature
+import com.tramnt.genart.domain.repository.AiArtRepository
+import kotlinx.coroutines.flow.Flow
+import kotlinx.coroutines.flow.flow
+import okhttp3.MediaType.Companion.toMediaTypeOrNull
+import okhttp3.MultipartBody
+import okhttp3.RequestBody.Companion.asRequestBody
+import okhttp3.RequestBody.Companion.toRequestBody
+import java.io.File
+import java.io.FileOutputStream
+
+class AiArtRepositoryImpl(
+ private val context: Context,
+ private val apiService: AiArtApiService
+) : AiArtRepository {
+
+ override suspend fun generateAiArt(
+ imageUri: Uri,
+ styleId: String,
+ prompt: String,
+ signature: Signature
+ ): Flow<Result<AiArt>> = flow {
+ try {
+ // Convert Uri to File
+ val file = uriToFile(imageUri)
+
+ // Create multipart request
+ val requestFile = file.asRequestBody("image/*".toMediaTypeOrNull())
+ val filePart = MultipartBody.Part.createFormData("file", file.name, requestFile)
+
+ // Create styleId and prompt request bodies
+ val styleIdPart = styleId.toRequestBody("text/plain".toMediaTypeOrNull())
+ val promptPart = prompt.toRequestBody("text/plain".toMediaTypeOrNull())
+
+ // Set authorization header with Bearer token
+ val authorization =
+ "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJwbGF0Zm9ybSI6ImlvcyIsImlhdCI6MTUxNjIzOTAyMn0.x6IpwVBb5g1bNLEsFjfGoghj0RVIhIXp2EGJaShka3k"
+
+ // Call API
+ val response = apiService.generateAiArt(
+ authorization = authorization,
+ signature = signature.signature,
+ timestamp = signature.timestamp,
+ keyId = signature.keyId,
+ file = filePart,
+ styleId = styleIdPart,
+ prompt = promptPart
+ )
+
+ // Convert response to domain model
+ val aiArt = response.toDomain()
+ if (aiArt != null) {
+ emit(Result.success(aiArt))
+ } else {
+ emit(Result.failure(Exception("Failed to generate AI art")))
+ }
+ } catch (e: Exception) {
+ emit(Result.failure(e))
+ }
+ }
+
+ private fun uriToFile(uri: Uri): File {
+ val inputStream = context.contentResolver.openInputStream(uri)
+ val tempFile = File.createTempFile("image", ".jpg", context.cacheDir)
+ val outputStream = FileOutputStream(tempFile)
+
+ inputStream?.use { input ->
+ outputStream.use { output ->
+ input.copyTo(output)
+ }
+ }
+
+ return tempFile
+ }
