+import java.util.concurrent.TimeUnit
+
+object NetworkModule {
+ private const val STYLE_API_BASE_URL = "https://api-style-manager.apero.vn/"
+ private const val SIGNATURE_API_BASE_URL = "https://image-generator.dev.apero.vn/"
+ private const val AI_ART_API_BASE_URL = "https://image-generator.dev.apero.vn/"
+ private const val TIMEOUT = 30L
+
+ private val loggingInterceptor = HttpLoggingInterceptor().apply {
+ level = HttpLoggingInterceptor.Level.BODY
+ }
+
+ private fun createOkHttpClient() = OkHttpClient.Builder()
+ .connectTimeout(TIMEOUT, TimeUnit.SECONDS)
+ .readTimeout(TIMEOUT, TimeUnit.SECONDS)
+ .writeTimeout(TIMEOUT, TimeUnit.SECONDS)
+ .addInterceptor(loggingInterceptor)
+ .build()
+
+ private fun createStyleRetrofit() = Retrofit.Builder()
+ .baseUrl(STYLE_API_BASE_URL)
+ .client(createOkHttpClient())
+ .addConverterFactory(GsonConverterFactory.create())
+ .build()
+
+ private fun createSignatureRetrofit() = Retrofit.Builder()
+ .baseUrl(SIGNATURE_API_BASE_URL)
+ .client(createOkHttpClient())
+ .addConverterFactory(GsonConverterFactory.create())
+ .build()
+
+ private fun createAiArtRetrofit() = Retrofit.Builder()
+ .baseUrl(AI_ART_API_BASE_URL)
+ .client(createOkHttpClient())
+ .addConverterFactory(GsonConverterFactory.create())
+ .build()
