+ fun processIntent(intent: GenArtIntent) {
+ when (intent) {
+ is GenArtIntent.LoadStyles -> loadStyles()
+ is GenArtIntent.SelectStyle -> selectStyle(intent.styleId)
+ is GenArtIntent.SelectImage -> selectImage(intent.imageUri)
+ is GenArtIntent.SetPrompt -> setPrompt(intent.prompt)
+ is GenArtIntent.GenerateArt -> generateArt()
+ is GenArtIntent.Reset -> resetState()
+ }
+ }
+
+ private fun loadStyles() {
+ viewModelScope.launch {
+ _viewState.update { it.copy(isLoading = true) }
+
+ getStylesUseCase().collect { result ->
+ result.fold(
+ onSuccess = { styles ->
+ _viewState.update {
+ it.copy(
+ isLoading = false,
+ styleCategories = styles,
+ error = null
+ )
+ }
+ },
+ onFailure = { error ->
+ _viewState.update {
+ it.copy(
+ isLoading = false,
+ error = error.message
+ )
+ }
+ }
+ )
+ }
+ }
+ }
+
+ private fun selectStyle(styleId: String) {
+ val styles = _viewState.value.styleCategories.flatMap { it.styles }
+ val selectedStyle = styles.find { it.styleId == styleId }
+
+ _viewState.update {
+ it.copy(selectedStyleId = styleId, selectedStyle = selectedStyle)
+ }
+ }
+
+ private fun selectImage(uri: Uri) {
+ _viewState.update { it.copy(selectedImageUri = uri) }
+ }
+
+ private fun setPrompt(prompt: String) {
+ _viewState.update { it.copy(prompt = prompt) }
+ }
+
+ private fun resetState() {
+ _viewState.update {
+ it.copy(
+ selectedImageUri = null,
+ selectedStyleId = null,
+ selectedStyle = null,
+ generatedArt = null,
+ prompt = "",
+ isGenerating = false,
+ error = null
+ )
+ }
+ }
+
+ private fun getSignature() {
+ viewModelScope.launch {
+ _viewState.update { it.copy(isLoading = true) }
+
+ getSignatureUseCase(apiKey).collect { result ->
+ result.fold(
+ onSuccess = { signature ->
+ currentSignature = signature
+ generateArtWithSignature()
+ },
+ onFailure = { error ->
+ _viewState.update {
+ it.copy(
+ isLoading = false,
+ isGenerating = false,
+ error = "Failed to get signature: ${error.message}"
+ )
+ }
+ }
+ )
+ }
+ }
+ }
+
+ private fun generateArt() {
+ val currentState = _viewState.value
+
+ if (currentState.selectedImageUri == null) {
+ _viewState.update { it.copy(error = "Please select an image") }
+ return
+ }
+
+ if (currentState.selectedStyleId == null) {
+ _viewState.update { it.copy(error = "Please select a style") }
+ return
+ }
+
+ _viewState.update { it.copy(isGenerating = true, error = null) }
+
+ if (currentSignature == null) {
+ getSignature()
+ } else {
+ generateArtWithSignature()
+ }
+ }
+
+ private fun generateArtWithSignature() {
+ val currentState = _viewState.value
+ val signature = currentSignature ?: return
+
