+ currentState.selectedImageUri?.let { imageUri ->
+ currentState.selectedStyleId?.let { styleId ->
+ generateAiArtUseCase(
+ imageUri = imageUri,
+ styleId = styleId,
+ prompt = currentState.prompt,
+ signature = signature
+ ).collect { result ->
+ result.fold(
+ onSuccess = { aiArt ->
+ _viewState.update {
+ it.copy(
+ isLoading = false,
+ isGenerating = false,
+ generatedArt = aiArt,
+ error = null
+ )
+ }
+ },
+ onFailure = { error ->
+ _viewState.update {
+ it.copy(
+ isLoading = false,
+ isGenerating = false,
+ error = "Failed to generate art: ${error.message}"
+ )
+ }
+ }
+ )
+ }
+ }
+ }
+ }
+ }
