```kotlin:app/src/main/java/com/tramnt/genart/ui/genart/GenArtViewModel.kt
package com.tramnt.genart.ui.genart

import android.net.Uri
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.tramnt.genart.di.AppContainer
import com.tramnt.genart.domain.model.AiArt
import com.tramnt.genart.domain.model.Signature
import com.tramnt.genart.domain.model.StyleCategory
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.update
import kotlinx.coroutines.launch

class GenArtViewModel : ViewModel() {
 private val _viewState = MutableStateFlow(GenArtViewState())
 val viewState: StateFlow<GenArtViewState> = _viewState.asStateFlow()

 // API Key for signature
 private val apiKey = "sk-09adkagfD0nUyawNEUkS8pikFYElcdgkiYQGDQJcly8Ct8JHVH"

 // Use cases
 private val getStylesUseCase = AppContainer.getStyleCategoriesUseCase
 private val getSignatureUseCase = AppContainer.getSignatureUseCase
 private val generateAiArtUseCase = AppContainer.generateAiArtUseCase

 // Store signature once retrieved
 private var currentSignature: Signature? = null

 init {
 loadStyles()
 }

 fun onIntent(intent: GenArtIntent) {
 when (intent) {
 is GenArtIntent.LoadStyles -> loadStyles()
 is GenArtIntent.SelectStyle -> selectStyle(intent.styleId)
 is GenArtIntent.SelectImage -> selectImage(intent.imageUri)
 is GenArtIntent.SetPrompt -> setPrompt(intent.prompt)
 is GenArtIntent.GenerateArt -> generateArt()
 is GenArtIntent.Reset -> resetState()
 is GenArtIntent.PickImage -> getSignature()
 }
 }

 private fun loadStyles() {
 viewModelScope.launch {
 _viewState.update { it.copy(isLoading = true) }

 getStylesUseCase().collect { result ->
 result.fold(
 onSuccess = { styles ->
 _viewState.update {
 it.copy(
 isLoading = false,
 styleCategories = styles,
 error = null
 )
 }
 },
 onFailure = { error ->
 _viewState.update {
 it.copy(
 isLoading = false,
 error = error.message
 )
 }
 }
 )
 }
 }
 }

 private fun selectStyle(styleId: String) {
 val styles = _viewState.value.styleCategories.flatMap { it.styles }
 val selectedStyle = styles.find { it.styleId == styleId }

 _viewState.update {
 it.copy(selectedStyleId = styleId, selectedStyle = selectedStyle)
 }
 }

 private fun selectImage(uri: Uri) {
 _viewState.update { it.copy(selectedImageUri = uri) }
 }

 private fun setPrompt(prompt: String) {
 _viewState.update { it.copy(prompt = prompt) }
 }

 private fun resetState() {
 _viewState.update {
 it.copy(
 selectedImageUri = null,
 selectedStyleId = null,
 selectedStyle = null,
 generatedArt = null,
 prompt = "",
 isGenerating = false,
 error = null
 )
 }
 }

 private fun getSignature() {
 viewModelScope.launch {
 _viewState.update { it.copy(isLoading = true) }

 getSignatureUseCase(apiKey).collect { result ->
 result.fold(
 onSuccess = { signature ->
 currentSignature = signature
 generateArtWithSignature()
 },
 onFailure = { error ->
 _viewState.update {
 it.copy(
 isLoading = false,
 isGenerating = false,
 error = "Failed to get signature: ${error.message}"
 )
 }
 }
 )
 }
 }
 }

 private fun generateArt() {
 val currentState = _viewState.value

 if (currentState.selectedImageUri == null) {
 _viewState.update { it.copy(error = "Please select an image") }
 return
 }

 if (currentState.selectedStyleId == null) {
 _viewState.update { it.copy(error = "Please select a style") }
 return
 }

 _viewState.update { it.copy(isGenerating = true, error = null) }

 if (currentSignature == null) {
 getSignature()
 } else {
 generateArtWithSignature()
 }
 }

 private fun generateArtWithSignature() {
 val currentState = _viewState.value
 val signature = currentSignature ?: return

 viewModelScope.launch {
 currentState.selectedImageUri?.let { imageUri ->
 currentState.selectedStyleId?.let { styleId ->
 generateAiArtUseCase(
 imageUri = imageUri,
 styleId = styleId,
 prompt = currentState.prompt,
 signature = signature
 ).collect { result ->
 result.fold(
 onSuccess = { aiArt ->
 _viewState.update {
 it.copy(
 isLoading = false,
 isGenerating = false,
 generatedArt = aiArt,
 error = null
 )
 }
 },
 onFailure = { error ->
 _viewState.update {
 it.copy(
 isLoading = false,
 isGenerating = false,
 error = "Failed to generate art: ${error.message}"
 )
 }
 }
 )
 }
 }
 }
 }
 }
} 
