```kotlin:app/src/main/java/com/tramnt/genart/ui/genart/GenArtContract.kt
package com.tramnt.genart.ui.genart

import com.tramnt.genart.base.mvi.MviEffect
import com.tramnt.genart.base.mvi.MviIntent
import com.tramnt.genart.base.mvi.MviViewState

data class ViewState(
 val prompt: String = "",
 val selectedPhotoUri: String? = null,
 val selectedStyleIndex: Int = 0,
 val isGenerating: Boolean = false,
 val generatedImageUri: String? = null
) : MviViewState

sealed interface Intent : MviIntent {
 data class PromptChanged(val prompt: String) : Intent
 data class PhotoSelected(val uri: String) : Intent
 data class StyleSelected(val index: Int) : Intent
 data object GenerateClicked : Intent
 data object ClearPrompt : Intent
 data object ClearPhoto : Intent
}

sealed interface Effect : MviEffect {
 data class ShowToast(val message: String) : Effect
 data class NavigateToResult(val imageUri: String) : Effect
 data object ShowPhotoSelector : Effect
}

```
