 color = GenArtPink,
 fontWeight = FontWeight.Bold,
 fontSize = 18.sp
 )

 if (isLoading) {
 // Show loading indicator
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(100.dp),
 contentAlignment = Alignment.Center
 ) {
 CircularProgressIndicator(color = GenArtPink)
 }
 } else if (styleCategories.isEmpty()) {
 // Show empty state
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(100.dp),
 contentAlignment = Alignment.Center
 ) {
 Text(
 text = "No styles available",
 color = Color.Gray,
 fontSize = 14.sp
 )
 }
 } else {
 // Display API categories
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 styleCategories.forEachIndexed { index, category ->
 StyleTab(
 text = category.name,
 selected = selectedStyleIndex == index,
 onClick = { onStyleSelected(index) }
 )
 }
 }

 Spacer(modifier = Modifier.height(8.dp))

 // Display API styles
 StyleOptionsFromApi(styleCategories, selectedStyleIndex)
 }
 }
}

@Preview(showBackground = true)
@Composable
fun StyleSelectionSectionPreview() {
 StyleSelectionSection(
 selectedStyleIndex = 2,
 onStyleSelected = {}
 )
}

@Composable
fun StyleTab(text: String, selected: Boolean = false, onClick: () -> Unit = {}) {
 Text(
 text = text,
 color = if (selected) GenArtPink else Color.Gray,
 fontSize = 14.sp,
 fontWeight = FontWeight.Medium,
 modifier = Modifier
 .padding(vertical = 4.dp)
 .clickable(onClick = onClick)
 )
}

@Preview(showBackground = true)
@Composable
fun StyleTabPreview_Selected() {
 StyleTab(text = "Anime", selected = true, onClick = {})
}

@Preview(showBackground = true)
@Composable
fun StyleTabPreview_Unselected() {
 StyleTab(text = "Fashion", selected = false, onClick = {})
}



@Composable
fun StyleOptionsFromApi(styles: List<StyleCategory>, selectedIndex: Int) {
 LazyRow(
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 itemsIndexed(styles) { index, style ->
 StyleOptionItem(
 name = style.name,
 imageUrl = style.imageUrl,
 isSelected = index == selectedIndex
 )
 }
 }
}

@Composable
fun StyleOptionItem(name: String, imageUrl: String, isSelected: Boolean = false) {
 Column(
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Box {
 AsyncImage(
 model = ImageRequest.Builder(LocalContext.current)
 .data(imageUrl)
 .crossfade(true)
 .build(),
 contentDescription = name,
 modifier = Modifier
 .size(60.dp)
 .clip(CircleShape),
 contentScale = ContentScale.Crop
 )

 if (isSelected) {
 Box(
 modifier = Modifier
 .size(60.dp)
 .clip(CircleShape)
 .padding(2.dp),
 contentAlignment = Alignment.Center
 ) {
 Box(
 modifier = Modifier
 .size(56.dp)
 .clip(CircleShape)
 .padding(2.dp)
 )
 }
 }
 }

 Text(
 text = name,
 fontSize = 12.sp,
 color = if (isSelected) GenArtPink else Color.Black
 )
 }
}






