@Composable
fun StyleSelectionSection(
 selectedStyleIndex: Int,
 onStyleSelected: (Int) -> Unit,
 styleCategories: List<StyleCategory> = emptyList(),
 isLoading: Boolean = false
) {
 Column(
 modifier = Modifier.fillMaxWidth()
 ) {
 Text(
 text = "Choose your Style",
 color = GenArtPink,
 fontWeight = FontWeight.Bold,
 fontSize = 18.sp
 )

 if (isLoading) {
 // Show loading indicator
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(100.dp),
 contentAlignment = Alignment.Center
 ) {
 CircularProgressIndicator(color = GenArtPink)
 }
 } else if (styleCategories.isEmpty()) {
 // Show empty state
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .height(100.dp),
 contentAlignment = Alignment.Center
 ) {
 Text(
 text = "No styles available",
 color = Color.Gray,
 fontSize = 14.sp
 )
 }
 } else {
 // Display API categories
 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 styleCategories.forEachIndexed { index, category ->
 StyleTab(
 text = category.name,
 selected = selectedStyleIndex == index,
 onClick = { onStyleSelected(index) }
 )
 }
 }

 Spacer(modifier = Modifier.height(8.dp))

 // Display API styles in a grid-like fashion
 StyleGrid(styleCategories, selectedIndex = selectedStyleIndex, onStyleSelected)
 }
 }
}

