@Composable
fun StyleTab(text: String, selected: Boolean = false, onClick: () -> Unit = {}) {
 Text(
 text = text,
 color = if (selected) GenArtPink else Color.Gray,
 fontSize = 14.sp,
 fontWeight = FontWeight.Medium,
 modifier = Modifier
 .padding(vertical = 4.dp)
 .clickable(onClick = onClick)
 )
}

