package com.tramnt.genart.ui.genart

import androidx.lifecycle.viewModelScope
import com.tramnt.genart.base.mvi.MviViewModel
import com.tramnt.genart.di.AppContainer
import kotlinx.coroutines.flow.catch
import kotlinx.coroutines.launch

class GenArtViewModel :
 MviViewModel<Intent, ViewState, Effect>() {

 private val getStyleCategoriesUseCase = AppContainer.getStyleCategoriesUseCase

 override val initialState: ViewState = ViewState()

 init {
 processIntent(Intent.LoadStyleCategories)
 }

 override fun processIntent(intent: Intent) {
 when (intent) {
 is Intent.PromptChanged -> updatePrompt(intent.prompt)
 is Intent.PhotoSelected -> updateSelectedPhoto(intent.uri)
 is Intent.StyleSelected -> updateSelectedStyle(intent.index)
 Intent.GenerateClicked -> generateImage()
 Intent.ClearPrompt -> clearPrompt()
 Intent.ClearPhoto -> clearPhoto()
 Intent.LoadStyleCategories -> loadStyleCategories()
 }
 }

 private fun loadStyleCategories() {
 setState { copy(isLoadingStyles = true, errorMessage = null) }

 viewModelScope.launch {
 getStyleCategoriesUseCase()
 .catch { error ->
 setState {
 copy(
 isLoadingStyles = false,
 errorMessage = error.message ?: "Failed to load styles"
 )
 }
 sendEffect { Effect.ShowToast(error.message ?: "Failed to load styles") }
 }
 .collect { result ->
 result.fold(
 onSuccess = { categories ->
 setState {
 copy(
 isLoadingStyles = false,
 styleCategories = categories,
 errorMessage = null
 )
 }
 },
 onFailure = { error ->
 setState {
 copy(
 isLoadingStyles = false,
 errorMessage = error.message ?: "Failed to load styles"
 )
 }
 sendEffect {
 Effect.ShowToast(error.message ?: "Failed to load styles")
 }
 }
 )
 }
 }
 }

 private fun updatePrompt(prompt: String) {
 setState { copy(prompt = prompt) }
 }

 private fun updateSelectedPhoto(uri: String) {

 setState { copy(selectedPhotoUri = uri) }
 }

 private fun updateSelectedStyle(index: Int) {
 setState { copy(selectedStyleIndex = index) }
 }

 private fun generateImage() {
 setState { copy(isGenerating = true) }

 // Here would be the actual image generation logic
 viewModelScope.launch {
 // Simulate network delay
 kotlinx.coroutines.delay(2000)

 // Update state with generated image
 setState {
 copy(
 isGenerating = false,
 generatedImageUri = "https://example.com/generated.jpg"
 )
 }

 // Send effect to navigate to result
 sendEffect {
 Effect.NavigateToResult(
 "https://example.com/generated.jpg"
 )
 }
 }
 }

 private fun clearPrompt() {
 setState { copy(prompt = "") }
 }

 private fun clearPhoto() {
 // Just clear the photo without showing selector
 setState { copy(selectedPhotoUri = null) }
 }
}
